<?phpinclude_once 'header.php';include_once 'check_session.php';?><script>    function save(check)    {        var ques = $('#ques').val();        var clientname = $('#clientname').val();        var password = $('#password').val();        var allowexport = $("input[name=allowexport]").is(":checked");        var allowimport = $("input[name=allowimport]").is(":checked");        var returnedData = "";                if(ques == "" || clientname == "" || password == "") {             $("#err_msg").html("Some fields are missing");             return;        }                if(allowexport) {            allowexport_val = "";         } else {            allowexport_val = "on";        }                if(allowimport) {            allowimport_val = "";         } else {            allowimport_val = "on";        }        $.ajax({            method: "POST",            url: "API/index.php",            data: { type:"create_quest", ques:ques, clientname: clientname, password: password, allowexport: allowexport_val, allowimport: allowimport_val}        }).done(function( data ) {                       returnedData = JSON.parse(data);            if(returnedData.code == "0") {                if(check == "san") {                    location.href = "welcome-page.php?ques_id="+returnedData.ques_id;                } else {                    location.href = "admin.php";                }            } else {                $("#err_msg").html(returnedData.log);            }          });                     return returnedData;    }    $(document).ready(function(){        $('#saveandnext').click(function(event){            event.preventDefault();            save("san");        });                $('#saveandexit').click(function(event){            event.preventDefault();            save("sae");        });                $('#cancel').click(function(event){            event.preventDefault();            location.href = "admin.php";        });    });</script><div class="sec_create">    <h2 class="sec_head_ques"> Create a Questionnaire</h2></div><div class="wrapper"> <form action="#" method="post">    <div class="center">                    <div id="err_msg"></div>        <div class="top_data">            <div class="left_data">                <span>Questionnaire Name</span>                <div class="qsn_name">                    <input type="text" id="ques" name="ques" class="input">                </div>                <span>Client Login</span>                <div class="name">                    <input type="text" id="clientname" name="clientname" class="input" placeholder="Username">                </div>                <div class="pass">                    <input type="text" id="password" name="password" class="input" placeholder="Password">                </div>            </div>            <div class="right_data">                <div class="run_time">                    <span>Questionnaire Run Time</span>                    <h2>11/7/15, 7:00am-8:00am                        <button class="bb_img"> <img src="images/minus.jpg"/></button></h2>                    <button class="bb_img"><img src="images/add.jpg"/></button>                    <span> add time</span>                </div>                <div class="export">                    <span>Allow client to export?</span>                    <div class="onoffswitch">                        <input type="checkbox" name="allowexport" class="onoffswitch-checkbox" id="tab4" checked>                        <label class="onoffswitch-label" for="tab4">                            <span class="onoffswitch-inner"></span>                            <span class="onoffswitch-switch"></span>                        </label>                    </div>                </div>                <div class="import">                    <span>Allow client to import?</span>                    <div class="onoffswitch">                        <input type="checkbox" name="allowimport" class="onoffswitch-checkbox" id="tab5" checked>                        <label class="onoffswitch-label" for="tab5">                            <span class="onoffswitch-inner"></span>                            <span class="onoffswitch-switch"></span>                        </label>                    </div>                </div>            </div>        </div>        <div class="clear"></div>        <div class="lastouter_sec">            <button class="butt_view bb_sec6" id="saveandnext"> Save&Next </button>            <button class="butt_view bb_sec6" id="saveandexit"> Save&Exit </button>            <button class="butt_view bb_sec6 bb_bg" id="cancel"> Cancel </button>        </div>    </div> </form></div></body></html>